<style>
    header {
        width: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        gap: 0.75rem;
        padding: 0.75rem 0;
        border-bottom: 2px solid var(--accent-primary);
        transition: gap 200ms;
        background-color: var(--bg-secondary);
        box-shadow: 0 4px 6px black;
    }

    .heading {
        position: relative;
        display: flex;
        align-items: center;
        gap: 1.25rem;
        transition: gap 200ms;
    }

    h1 {
        font-size: clamp(1.5rem, 6.5vw, 2.5rem);
        font-weight: 800;
        white-space: nowrap;
    }

    span {
        color: var(--accent-primary);
        font-size: 1.5rem;
    }

    svg {
        transition: height 200ms;
    }

    .user {
        position: absolute;
        right: 1.5rem;
        font-size: 1.25rem;
        font-weight: 600;
    }

    @media screen and (max-width: 500px) {
        header {
            gap: 0;
            justify-content: space-around;
        }
        .heading {
            gap: 0.5rem;
        }
        span {
            display: none;
        }
        svg {
            height: 40px;
        }
    }
    @media screen and (max-width: 1100px) {
        .user {
            display: none;
        }
    }
</style>
